changeSet.sql:
create table movie_entity (id bigint generated by default as identity, title varchar(255), primary key (id));
create table rating_entity (id bigint generated by default as identity, name varchar(255), rating double not null, user_views bigint not null, movie_id bigint, internal_id bigint, primary key (id));
alter table rating_entity add constraint FK_RATING_MOVIE foreign key (movie_id) references movie_entity;
